argh = argh.replace("<br>", "\n").replace("</font>", "")


def convertcolor(m):
    htmlcolor = m.group(1)
    if htmlcolor == "white":
        htmlcolor = "#ffffff"
    elif htmlcolor == "black":
        htmlcolor = "#000000"
    elif htmlcolor == "gray":
        htmlcolor = "#808080"
    return dlstbx.util.html2ansi.hex2col(htmlcolor)


swirly = re.sub("<font color=([^>]+)>", convertcolor, argh)
swirly_lines = swirly.split("\n")

parser = OptionParser()
parser.add_option("-?", action="help", help=SUPPRESS_HELP)
parser.add_option(
    "--forever",
    dest="forever",
    action="store_true",
    default=False,
    help="Waaah without end.",
)

options, args = parser.parse_args()

while options.forever:
    l = swirly_lines.pop(0)
    print(l)
    swirly_lines.append(l)
    time.sleep(0.05)
print(swirly)
sys.stdout.write("\033[0m")
